<?phpnamespace App\Models;use Illuminate\Database\Eloquent\Model;class QuestionContents extends Model{    const ACTIVE = 1;    protected $fillable = [        'question_id',        'content_id',        'type'    ];    public $timestamps = false;    public function questions() {        return $this->belongsTo(Questions::class, 'question_id');    }    public function contents()    {        return $this->belongsTo(Contents::class, 'content_id');    }}