<?phpnamespace App\Models;use Illuminate\Database\Eloquent\Model;class Questions extends Model{    const TYPE_ABOUT = 0;    const TYPE_NEWS = 1;    const TYPE_SERVICE = 2;    const TYPE_PRICE = 3;    const TYPE_TERM = 4;    const TYPE_PRODUCT = 5;    const ACTIVE = 1;    protected $fillable = [        'parent_id',        'intro',        'status',        'name',        'slug',        'sort',        'image',        'alt',        'meta',    ];    public function parent()    {        return $this->belongsTo(Questions::class, 'parent_id')->first();    }    public function child()    {        return $this->hasMany(Questions::class, 'parent_id');    }    public function questionContents()    {        return $this->hasMany(QuestionContents::class, 'question_id');    }    public function contents()    {        return $this->belongsToMany(Contents::class, 'question_contents', 'question_id', 'content_id');    }    public function prices()    {        return $this->belongsToMany(Prices::class, 'question_contents', 'question_id', 'content_id');    }    public function services()    {        return $this->belongsToMany(Services::class, 'question_contents', 'question_id', 'content_id');    }    public function introduces()    {        return $this->belongsToMany(Introduces::class, 'question_contents', 'question_id', 'content_id');    }}