<?phpnamespace App\Models;use Illuminate\Database\Eloquent\Model;class RelatedItem extends Model{    const TYPE_HOTEL = 0;    const TYPE_NEWS = 1;    const TYPE_SERVICE = 2;    const ACTIVE = 1;    protected $fillable = [        'related_id',        'related_type',        'item_id',        'item_type'    ];    public function items()    {        return $this->morphedByMany(Hotels::class, 'item', 'related_items');    }    public function products()    {        return $this->morphedByMany(Hotels::class, 'related', 'related_items');    }    public function news()    {        return $this->morphedByMany(Contents::class, 'related', 'related_items');    }}