            $('form')                .on('blur', 'input[required], input.optional, select.required', validator.checkField)                .on('change', 'select.required', validator.checkField)                .on('keypress', 'input[required][pattern]', validator.keypress);            $('.multi.required').on('keyup blur', 'input', function () {                validator.checkField.apply($(this).siblings().last()[0]);            });            $('form').submit(function (e) {                e.preventDefault();                var submit = true;                // evaluate the form using generic validaing                if (!validator.checkAll($(this))) {                    submit = false;                }                if (submit)                    this.submit();                return false;            });            //Show image to box when select            function show_img_selected(input) {                if (input.files && input.files[0]) {                    var reader = new FileReader();                    reader.onload = function (e) {                        $('#img_show').attr('src', e.target.result)                    };                    reader.readAsDataURL(input.files[0]);                }            }            function show_img_selected2(input) {                if (input.files && input.files[0]) {                    var reader = new FileReader();                    reader.onload = function (e) {                        $('#img_show2').attr('src', e.target.result)                    };                    reader.readAsDataURL(input.files[0]);                }            }            function show_img_selected3(input) {                if (input.files && input.files[0]) {                    var reader = new FileReader();                    reader.onload = function (e) {                        $('#img_show3').attr('src', e.target.result)                    };                    reader.readAsDataURL(inpu