"{{asset('libs/validator/validator.js')}}"></script>    <script>        // initialize the validator function        validator.message.date = 'not a real date';        // validate a field on "blur" event, a 'select' on 'change' event & a '.reuired' classed multifield on 'keyup':        $('form')            .on('blur', 'input[required], input.optional, select.required', validator.checkField)            .on('change', 'select.required', validator.checkField)            .on('keypress', 'input[required][pattern]', validator.keypress);        $('.multi.required').on('keyup blur', 'input', function () {            validator.checkField.apply($(this).siblings().last()[0]);        });        $('form').submit(function (e) {            e.preventDefault();            var submit = true;            // evaluate the form using generic validaing            if (!validator.checkAll($(this))) {                submit = false;            }            if (submit)                this.submit();            return false;        });        //Show image to box when select        function show_img_selected(input) {            if (input.files && input.files[0]) {                var reader = new FileReader();                reader.onload = function (e) {                    $('#img_show').attr('src', e.target.result)                };                reader.readAsDataURL(input.files[0]);            }        }    </script>    <script>        $('#choose-type').select2();        CKEDITOR.replace('editor2', {            filebrowserUploadUrl: "{{route('upload', ['_token' => csrf_token() ])}}",            filebrowserUploadMethod: 'form',        });        CKEDITOR.replace('editor22', {            filebrowserUploadUrl: "{{route('upload', ['_token' => csrf_token() ])}}",            filebrowserUploadMethod: 'form',        });    </script>@endpush